INSERT INTO runninghi.tbl_feedback (title, content, has_reply, reply, category, member_no)
VALUES
    ('거리 측정 오류', '앱에서 측정한 러닝 거리가 실제보다 짧게 나옵니다. GPS 정확도를 개선해 주세요.', false, NULL, 'ROUTEERROR', 11244),
    ('새로운 기능 제안', '러닝 중 음성 안내 기능이 있으면 좋겠어요. 페이스와 거리를 주기적으로 알려주면 동기부여가 될 것 같습니다.', true, '좋은 제안 감사합니다. 다음 업데이트에 음성 안내 기능을 추가할 예정입니다.', 'PROPOSAL', 11244),
    ('앱 사용 문의', '친구와 함께 러닝한 기록을 공유하는 방법을 알고 싶습니다.', false, NULL, 'INQUIRY', 11244),
    ('칼로리 계산 정확도', '러닝 후 칼로리 소모량이 다른 앱들과 차이가 많이 납니다. 계산 방식을 알려주시거나 보정해 주실 수 있나요?', true, '현재 체중, 신장, 나이를 기반으로 계산하고 있습니다. 더 정확한 측정을 위해 심박수 데이터를 활용하는 방안을 검토 중입니다.', 'INQUIRY', 11244),
    ('UI 개선 요청', '러닝 시작 버튼이 너무 작아서 누르기 어려워요. 좀 더 크게 만들어 주세요.', false, NULL, 'PROPOSAL', 11244),
    ('배터리 소모 문제', '앱 사용 중 배터리가 너무 빨리 닳아요. 배터리 최적화가 필요해 보입니다.', true, '배터리 소모 최적화 작업을 진행 중입니다. 다음 업데이트에서 개선될 예정입니다.', 'PROPOSAL', 11244),
    ('러닝화 추천 기능', '제 러닝 스타일에 맞는 러닝화를 추천해주는 기능이 있으면 좋겠어요.', false, NULL, 'PROPOSAL', 11244),
    ('날씨 정보 연동', '러닝 전 날씨 정보를 앱에서 바로 확인할 수 있게 해주세요.', true, '날씨 정보 연동 기능을 개발 중입니다. 곧 새로운 기능으로 만나보실 수 있을 거예요.', 'PROPOSAL', 11244),
    ('러닝 코스 공유', '제가 자주 뛰는 좋은 코스를 다른 사용자들과 공유하고 싶어요.', false, NULL, 'PROPOSAL', 11244),
    ('앱 충돌 문제', '러닝 중간에 가끔 앱이 멈춰요. 안정성 개선이 필요합니다.', true, '불편을 드려 죄송합니다. 현재 안정성 개선 작업을 최우선으로 진행 중입니다.', 'WEBERROR', 11244),
    ('목표 설정 기능', '월간, 연간 러닝 목표를 설정하고 진행 상황을 확인할 수 있는 기능이 있으면 좋겠어요.', false, NULL, 'PROPOSAL', 11244),
    ('친구 추가 오류', '러닝 친구를 추가하려고 하는데 계속 오류가 발생합니다.', true, '해당 문제를 확인 중입니다. 곧 수정하여 업데이트 하겠습니다.', 'WEBERROR', 11246),
    ('음악 플레이어 연동', '러닝 중에 좋아하는 음악을 들을 수 있게 음악 앱과 연동해주세요.', false, NULL, 'PROPOSAL', 11246),
    ('러닝 페이스 조절 도움', '목표 페이스를 설정하면 러닝 중 음성으로 페이스 조절을 도와주는 기능이 있으면 좋겠어요.', true, '좋은 아이디어네요. 개발 계획에 반영하도록 하겠습니다.', 'PROPOSAL', 11246),
    ('데이터 동기화 문제', '새 폰으로 바꾼 후 기존 러닝 기록이 동기화되지 않아요.', false, NULL, 'WEBERROR', 11246),
    ('러닝 동호회 기능', '비슷한 수준의 러너들과 온라인 동호회를 만들 수 있는 기능이 있으면 좋겠어요.', true, '커뮤니티 기능 추가를 검토 중입니다. 좋은 제안 감사합니다.', 'PROPOSAL', 11246),
    ('걷기 모드 추가', '가끔은 걷기만 하고 싶을 때가 있어요. 걷기 모드도 추가해주세요.', false, NULL, 'PROPOSAL', 11246),
    ('광고 과다', '무료 버전의 광고가 너무 많아요. 러닝에 집중하기 어려워요.', true, '불편을 드려 죄송합니다. 광고 노출 빈도를 조정하고 있습니다. 프리미엄 버전도 고려해보세요.', 'PROPOSAL', 11246),
    ('마일리지 프로그램', '러닝 거리에 따라 마일리지를 적립하고 상품으로 교환할 수 있는 프로그램은 어떨까요?', false, NULL, 'PROPOSAL', 11246),
    ('앱 디자인', '전체적인 앱 디자인이 좀 올드해 보여요. 좀 더 현대적으로 개선해주세요.', true, '디자인 개선을 위한 작업을 진행 중입니다. 다음 대규모 업데이트에서 새로운 디자인을 만나보실 수 있을 거예요.', 'PROPOSAL', 11246);